package com.yanfei1819.configymlread.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;/** * Created by shiyanfei on 2021-04-09. */@Controller@RequestMapping("/yml")public class YmlController {    @Value("${student.name}")    private static String name;    @GetMapping("/readString")    public void readString(){        System.out.println(name);    }}