SET NAMES utf8mb4;SET FOREIGN_KEY_CHECKS = 0;-- ------------------------------ Table structure for student-- ----------------------------DROP TABLE IF EXISTS `student`;CREATE TABLE `student` (  `id` bigint(40) NOT NULL AUTO_INCREMENT,  `name` varchar(40) COLLATE utf8_bin DEFAULT NULL,  `age` int(3) DEFAULT NULL,  `call_no` varchar(20) COLLATE utf8_bin DEFAULT NULL,  `sex` varchar(1) COLLATE utf8_bin DEFAULT NULL COMMENT '性别：1-男，2-女',  `address` varchar(256) COLLATE utf8_bin DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;SET FOREIGN_KEY_CHECKS = 1;