package com.yanfei1819.mybatis.streaming.query.controller;import com.yanfei1819.mybatis.streaming.query.mapper.StudentMapper;import com.yanfei1819.mybatis.streaming.query.pojo.Student;import org.apache.ibatis.cursor.Cursor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.transaction.annotation.Transactional;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;/** * Created by shiyanfei on 2021-04-17. * * 仿照：https://mp.weixin.qq.com/s/3miuZuhcXNiNQiYH0vh6Wg */@Controller@RequestMapping("/api/student")@Transactionalpublic class StudentController {    @Autowired    private StudentMapper studentMapper;    @GetMapping("/queryAll")    public void queryAll(){        Cursor<Student> cursor = studentMapper.queryAll();        for (Student student : cursor) {            System.out.println(student.getAddress());        }    }}